import{_ as a,h as o,a9 as i,o as t}from"./chunks/framework.iNNLCK4S.js";const m=JSON.parse(`{"title":"在 Mac 邮件客户端添加 QQ 邮箱","description":"","frontmatter":{"head":[["script",{"charset":"UTF-8","id":"LA_COLLECT","src":"//sdk.51.la/js-sdk-pro.min.js"}],["script",{},"typeof LA !== 'undefined' && LA.init({\\"id\\":\\"3LNfUkScYzEz6k4D\\",\\"ck\\":\\"3LNfUkScYzEz6k4D\\",\\"hashMode\\":true})"]]},"headers":[],"relativePath":"daily-notes/issue-53.md","filePath":"daily-notes/issue-53.md","lastUpdated":null}`),l={name:"daily-notes/issue-53.md"};function c(d,e,s,r,n,p){return t(),o("div",null,e[0]||(e[0]=[i('<h1 id="在-mac-邮件客户端添加-qq-邮箱" tabindex="-1">在 Mac 邮件客户端添加 QQ 邮箱 <a class="header-anchor" href="#在-mac-邮件客户端添加-qq-邮箱" aria-label="Permalink to &quot;在 Mac 邮件客户端添加 QQ 邮箱&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">原文地址</p><p><a href="https://github.com/maomao1996/daily-notes/issues/53" target="_blank" rel="noreferrer">在 Mac 邮件客户端添加 QQ 邮箱 | GitHub</a></p></div><p>记录下在 Mac 邮件客户端添加 QQ 邮箱时遇到的问题（输入账号密码提示<strong>无法验证账户名或密码</strong>）</p><p><img src="https://i0.wp.com/maomao1996.github.io/daily-notes/static/53/53-1.png" alt="输入账号密码提示无法验证账户名或密码" loading="lazy"></p><p>这是因为 QQ 邮箱需要开启 <code>POP3/IMAP/SMTP/Exchange/CardDAV 服务</code> 并生成授权码，然后使用授权码登录</p><h2 id="生成-qq-邮箱授权码" tabindex="-1">生成 QQ 邮箱授权码 <a class="header-anchor" href="#生成-qq-邮箱授权码" aria-label="Permalink to &quot;生成 QQ 邮箱授权码&quot;">​</a></h2><ol><li>打开 <a href="https://mail.qq.com" target="_blank" rel="noreferrer">QQ 邮箱</a></li><li>点击 <code>设置</code></li><li>点击 <code>账号与安全</code></li><li>点击 <code>安全设置</code></li><li>开启 <code>POP3/IMAP/SMTP/Exchange/CardDAV 服务</code></li><li>点击 <code>生成授权码</code></li><li>复制授权码</li></ol><h2 id="添加-qq-邮箱" tabindex="-1">添加 QQ 邮箱 <a class="header-anchor" href="#添加-qq-邮箱" aria-label="Permalink to &quot;添加 QQ 邮箱&quot;">​</a></h2><ol><li>打开 <code>邮件</code> 应用</li><li>点击左上角 <code>邮件</code> — <code>添加账户</code></li><li>选择 <code>QQ 邮箱</code></li><li>输入名称和邮箱地址</li><li>密码输入上一步生成的<code>授权码</code></li><li>点击 <code>登录</code></li></ol>',9)]))}const u=a(l,[["render",c]]);export{m as __pageData,u as default};
