import{_ as e,h as t,o as a,a9 as o}from"./chunks/framework.35Ps3iiG.js";const f=JSON.parse('{"title":"微信开发踩坑记录","description":"记录个人遇到或他人分享的微信开发踩坑记录相关踩坑记录","frontmatter":{"description":"记录个人遇到或他人分享的微信开发踩坑记录相关踩坑记录"},"headers":[],"relativePath":"pit/wechat.md","filePath":"pit/wechat.md","lastUpdated":1700821602000}'),r={name:"pit/wechat.md"},i=o('<h1 id="微信开发踩坑记录" tabindex="-1">微信开发踩坑记录 <a class="header-anchor" href="#微信开发踩坑记录" aria-label="Permalink to &quot;微信开发踩坑记录&quot;">​</a></h1><p>记录个人遇到或他人分享的微信开发踩坑记录相关踩坑记录</p><h2 id="微信-h5-页面分享出去是明文链接不是卡片" tabindex="-1">微信 H5 页面分享出去是明文链接不是卡片 <a class="header-anchor" href="#微信-h5-页面分享出去是明文链接不是卡片" aria-label="Permalink to &quot;微信 H5 页面分享出去是明文链接不是卡片&quot;">​</a></h2><p>在微信开发者工具中，点击右上角的分享按钮，选择转发给朋友，可以分享出去的是卡片，但在手机上进行分享操作时，分享出去的是明文链接</p><div class="warning custom-block"><p class="custom-block-title">原因</p><p>微信开放全域名访问后出现的限制，在聊天窗口直接通过点击链接进入页面进行转发时会被微信拦截，导致分享出去的是明文链接</p><p><a href="https://mp.weixin.qq.com/s/7zxqex8DuuWj8cFfDhz2bw" target="_blank" rel="noreferrer">关于《微信外部链接内容管理规范》的更新说明</a></p></div><p><strong>解决方法</strong>：</p><blockquote><p>不直接通过点击群聊单聊里面的明文链接进入页面，而是通过其他方式进入页面</p></blockquote><ol><li>将链接配置在公众号菜单中，通过菜单进入页面</li><li>将链接通过聊天发送给公众号，再点击链接进入页面</li><li>生成二维码，通过扫描二维码进入页面</li><li>将链接添加到收藏夹，通过收藏夹进入页面</li></ol><p><a href="https://developers.weixin.qq.com/community/develop/doc/0004467e384530f11c2d610d655c00" target="_blank" rel="noreferrer">公众号分享出去不是卡片而是链接 | 微信开放社区</a></p>',9),c=[i];function l(s,p,n,d,_,h){return a(),t("div",null,c)}const u=e(r,[["render",l]]);export{f as __pageData,u as default};
